namespace DechetEquipementBundle\Repository;

/**
 * FournisseurRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class FournisseurRepository extends \Doctrine\ORM\EntityRepository
{
    /*
     * Trier les fournisseurs selon leur nom, utilisÃ© pour les formulaires.
     */
    public function trierParNom()
    {
        return $this->createQueryBuilder('f')
            ->orderBy('f.nom', 'ASC') ;
    }
}
